import React, { useEffect, useState } from 'react';
import { Table, Spinner, Button, Form } from 'react-bootstrap';
import api from '../../services/api';

export default function MaintenanceRequests() {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusUpdates, setStatusUpdates] = useState({});

  const fetchRequests = async () => {
    try {
      const res = await api.get('/maintenance');
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  const updateStatus = async (id) => {
    const newStatus = statusUpdates[id];
    if (!newStatus) return;
    try {
      await api.patch(`/maintenance/${id}`, { status: newStatus });
      fetchRequests();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <Spinner animation="border" />;

  return (
    <div>
      <h3>Maintenance Requests</h3>
      {requests.length === 0 ? <p>No maintenance requests.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Requested By</th>
              <th>Type</th>
              <th>Description</th>
              <th>Status</th>
              <th>Update Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {requests.map(r => (
              <tr key={r._id}>
                <td>{r.user?.name}</td>
                <td>{r.type}</td>
                <td>{r.description}</td>
                <td>{r.status}</td>
                <td>
                  <Form.Select
                    value={statusUpdates[r._id] || r.status}
                    onChange={e => setStatusUpdates(prev => ({ ...prev, [r._id]: e.target.value }))}
                  >
                    <option value="pending">Pending</option>
                    <option value="ongoing">Ongoing</option>
                    <option value="done">Done</option>
                  </Form.Select>
                </td>
                <td>
                  <Button variant="primary" onClick={() => updateStatus(r._id)}>
                    Update
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
