import React from 'react';
import './Features.css';

const Features = () => {
  const features = [
    {
      icon: 'fas fa-bell',
      title: 'Digital Notice Board',
      description: 'Important announcements and updates delivered directly to residents'
    },
    {
      icon: 'fas fa-credit-card',
      title: 'Payment System',
      description: 'Pay maintenance fees online and receive digital receipts instantly'
    },
    {
      icon: 'fas fa-calendar-check',
      title: 'Facility Booking',
      description: 'Reserve society facilities like clubhouse and event halls online'
    },
    {
      icon: 'fas fa-tools',
      title: 'Maintenance Requests',
      description: 'Submit maintenance issues and track resolution progress'
    },
    {
      icon: 'fas fa-shield-alt',
      title: 'Visitor Management',
      description: 'Inform security about visitors and maintain digital visitor logs'
    },
    {
      icon: 'fas fa-chart-pie',
      title: 'Financial Transparency',
      description: 'Track society expenses and income with detailed breakdowns'
    }
  ];

  return (
    <section id="features" className="features-section">
      <div className="container">
        <div className="row text-center mb-5">
          <div className="col">
            <h2 className="section-title">Key Features</h2>
            <p className="section-subtitle">Everything you need to manage your residential society efficiently</p>
          </div>
        </div>
        
        <div className="row g-4">
          {features.map((feature, index) => (
            <div key={index} className="col-md-4">
              <div className="feature-card">
                <div className="feature-icon">
                  <i className={feature.icon}></i>
                </div>
                <h5 className="feature-title">{feature.title}</h5>
                <p className="feature-description">{feature.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
