import React from 'react';
import { NavLink } from 'react-router-dom';

export default function Sidebar({ links }) {
  return (
    <div className="sidebar vh-100 p-3 d-flex flex-column" style={{ width: 250, backgroundColor: 'var(--color-dark-blue)', color: 'var(--color-off-white)' }}>
      <h3 className="mb-4">LivingLink</h3>
      <nav className="flex-grow-1">
        {links.map(({ to, label }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `d-block mb-2 p-2 rounded ${isActive ? 'bg-primary text-white' : 'text-light'}`
            }
          >
            {label}
          </NavLink>
        ))}
      </nav>
    </div>
  );
}
