// frontend/src/components/Sidebar.js
import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Sidebar({ links }) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div 
      className={`sidebar p-3 d-flex flex-column position-relative ${isCollapsed ? 'collapsed' : ''}`}
      style={{ 
        width: isCollapsed ? 60 : 250, 
        backgroundColor: 'var(--color-dark-blue)', 
        color: 'var(--color-off-white)',
        transition: 'width 0.3s ease',
        overflow: 'hidden',
        minHeight: '100vh' // This makes it take full height of container
      }}
    >
      {/* Menu Toggle Button */}
      <button
        onClick={toggleSidebar}
        className="btn btn-sm btn-outline-light position-absolute"
        style={{ top: 15, right: 15, zIndex: 1000 }}
      >
        {isCollapsed ? '→' : '←'}
      </button>

      {/* Logo - Only show when expanded */}
      {!isCollapsed && (
        <h3 className="mb-4">LivingLink</h3>
      )}
      
      {/* Navigation Links - Only show when expanded */}
      {!isCollapsed && (
        <nav className="flex-grow-1">
          {links.map(({ to, label }) => (
            <NavLink
              key={to}
              to={to}
              className={({ isActive }) =>
                `d-block mb-2 p-2 rounded text-decoration-none ${isActive ? 'bg-primary text-white' : 'text-light'}`
              }
            >
              {label}
            </NavLink>
          ))}
        </nav>
      )}
    </div>
  );
}
