import React, { useEffect, useState } from 'react';
import { Table, Spinner, Button, Form } from 'react-bootstrap';
import api from '../../services/api';

export default function SecretaryComplaints() {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusUpdates, setStatusUpdates] = useState({}); // id -> status

  const fetchComplaints = async () => {
    try {
      const res = await api.get('/complaints');
      setComplaints(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComplaints();
  }, []);

  const updateStatus = async (id) => {
    const newStatus = statusUpdates[id];
    if (!newStatus) return;
    try {
      await api.patch(`/complaints/${id}`, { status: newStatus });
      fetchComplaints();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <Spinner animation="border" />;

  return (
    <div>
      <h3>Complaints Management</h3>
      {complaints.length === 0 ? <p>No complaints found.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Complaint</th>
              <th>Status</th>
              <th>Update Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {complaints.map(c => (
              <tr key={c._id}>
                <td>{c.text}</td>
                <td>{c.status}</td>
                <td>
                  <Form.Select 
                    value={statusUpdates[c._id] || c.status} 
                    onChange={e => setStatusUpdates(prev => ({ ...prev, [c._id]: e.target.value }))}>
                    <option value="pending">Pending</option>
                    <option value="ongoing">Ongoing</option>
                    <option value="done">Done</option>
                  </Form.Select>
                </td>
                <td>
                  <Button variant="primary" onClick={() => updateStatus(c._id)}>
                    Update
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
