import React, { useEffect, useState } from 'react';
import { Table, Spinner, Form, Button, Alert } from 'react-bootstrap';
import api from '../../services/api';

export default function TreasurerFinances() {
  const [finances, setFinances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchFinances = async () => {
    try {
      const res = await api.get('/finances');
      setFinances(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFinances();
  }, []);

  const submitFinance = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!description || !amount) {
      setError('Please fill all fields');
      return;
    }
    try {
      await api.post('/finances', { description, amount: parseFloat(amount) });
      setSuccess('Finance entry added');
      setDescription('');
      setAmount('');
      fetchFinances();
    } catch (err) {
      setError(err.response?.data?.message || 'Error adding finance entry');
    }
  };

  if (loading) return <Spinner animation="border" />;

  return (
    <div>
      <h3>Society Finances</h3>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form onSubmit={submitFinance} className="mb-4">
        <Form.Group className="mb-2">
          <Form.Label>Description</Form.Label>
          <Form.Control 
            type="text" 
            value={description} 
            onChange={e => setDescription(e.target.value)} 
            required 
          />
        </Form.Group>
        <Form.Group className="mb-2">
          <Form.Label>Amount</Form.Label>
          <Form.Control 
            type="number" 
            step="0.01" 
            value={amount} 
            onChange={e => setAmount(e.target.value)} 
            required 
          />
        </Form.Group>
        <Button type="submit">Add Entry</Button>
      </Form>

      {finances.length === 0 ? <p>No finance entries.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Description</th>
              <th>Amount</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {finances.map(f => (
              <tr key={f._id}>
                <td>{f.description}</td>
                <td>{f.amount}</td>
                <td>{new Date(f.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
