import React from 'react';
import './Testimonials.css';

const Testimonials = () => {
  const testimonials = [
    {
      text: "LivingLink has transformed how our society operates. The payment system alone has saved us countless hours of manual work.",
      author: "Rajesh Kumar",
      role: "Society Secretary, Green Valley Homes"
    },
    {
      text: "As a treasurer, the financial transparency features have made it so much easier to manage our society's budget and expenses.",
      author: "Priya Singh",
      role: "Treasurer, Sunshine Apartments"
    },
    {
      text: "The maintenance request system is fantastic! Issues get resolved quickly, and we can track the status in real-time.",
      author: "Amit Patel",
      role: "Resident, Harmony Heights"
    }
  ];

  return (
    <section id="testimonials" className="testimonials-section">
      <div className="container">
        <div className="row text-center mb-5">
          <div className="col">
            <h2 className="section-title">What Our Users Say</h2>
            <p className="section-subtitle">Hear from society members who are using LivingLink</p>
          </div>
        </div>
        
        <div className="row">
          {testimonials.map((testimonial, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="testimonial-card">
                <p className="testimonial-text">"{testimonial.text}"</p>
                <div>
                  <div className="testimonial-author">{testimonial.author}</div>
                  <div className="testimonial-role">{testimonial.role}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
