import React, { useState } from 'react';
import { Container, Form, Button, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { setToken } from '../services/auth';

export default function LoginPage() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      setToken(res.data.token);
      const role = res.data.user.role;
      switch(role) {
        case 'resident': navigate('/resident'); break;
        case 'secretary': navigate('/secretary'); break;
        case 'treasurer': navigate('/treasurer'); break;
        case 'security': navigate('/security'); break;
        case 'maintenance': navigate('/maintenance'); break;
        default: navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ height: '90vh' }}>
      <Card style={{ width: '400px' }}>
        <Card.Body>
          <h3 className="mb-4 text-center">Login</h3>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={onSubmit}>
            <Form.Group className="mb-3" controlId="email">
              <Form.Label>Email address</Form.Label>
              <Form.Control 
                type="email" 
                name="email" 
                value={form.email}
                onChange={onChange} 
                placeholder="Enter email" 
                required 
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control 
                type="password" 
                name="password" 
                value={form.password}
                onChange={onChange} 
                placeholder="Password" 
                required 
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100">
              Login
            </Button>
          </Form>
          <div className="mt-3 text-center">
            Don't have an account? <a href="/register">Register</a>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
}
