import React, { useEffect, useState } from 'react';
import api from '../../../utils/api';

export default function Notices() {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/notices')
      .then(res => {
        setNotices(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  if (loading) return <p>Loading notices...</p>;

  return (
    <div>
      <h3>Notices</h3>
      {notices.length === 0 && <p>No notices available.</p>}
      {notices.map((notice) => (
        <div key={notice._id} className="card mb-3">
          <div className="card-body">
            <h5 className="card-title">{notice.title}</h5>
            <p className="card-text">{notice.content}</p>
            <small className="text-muted">Posted by: {notice.postedBy?.name || 'Secretary'} on {new Date(notice.createdAt).toLocaleDateString()}</small>
          </div>
        </div>
      ))}
    </div>
  );
}
