import React, { useEffect, useState } from 'react';
import { Table, Spinner, Button } from 'react-bootstrap';
import api from '../../services/api';

export default function SecretaryBookings() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchBookings = async () => {
    try {
      const res = await api.get('/bookings');
      setBookings(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  const updateBookingStatus = async (id, status) => {
    try {
      await api.patch(`/bookings/${id}`, { status });
      fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <Spinner animation="border" />;

  return (
    <div>
      <h3>Facility Booking Requests</h3>
      {bookings.length === 0 ? <p>No booking requests.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Resident</th>
              <th>Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(b => (
              <tr key={b._id}>
                <td>{b.user?.name}</td>
                <td>{new Date(b.date).toLocaleString()}</td>
                <td>{b.status}</td>
                <td>
                  {b.status === 'pending' && (
                    <>
                      <Button variant="success" size="sm" className="me-2" onClick={() => updateBookingStatus(b._id, 'approved')}>
                        Approve
                      </Button>
                      <Button variant="danger" size="sm" onClick={() => updateBookingStatus(b._id, 'rejected')}>
                        Reject
                      </Button>
                    </>
                  )}
                  {b.status !== 'pending' && <span>No actions</span>}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
