import React, { useEffect, useState } from 'react';
import api from '../../../api/api';

export default function Payments() {
  const [payments, setPayments] = useState([]);
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  async function fetchPayments() {
    try {
      const { data } = await api.get('/payments/my-payments');
      setPayments(data);
    } catch {
      setPayments([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchPayments();
  }, []);

  async function handlePay(e) {
    e.preventDefault();
    setMessage('');
    if (!amount || amount <= 0) {
      setMessage('Please enter a valid amount.');
      return;
    }
    try {
      const { data } = await api.post('/payments', { amount: Number(amount) });
      setMessage('Payment successful! Receipt: ' + data.payment.receiptNumber);
      setAmount('');
      fetchPayments();
    } catch (err) {
      setMessage(err.response?.data?.message || 'Payment failed.');
    }
  }

  return (
    <div>
      <h3>Maintenance Fee Payments</h3>
      <form onSubmit={handlePay} className="mb-4">
        <div className="mb-3" style={{ maxWidth: 300 }}>
          <label>Amount</label>
          <input
            type="number"
            className="form-control"
            value={amount}
            onChange={e => setAmount(e.target.value)}
            min="1"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Pay Now</button>
      </form>
      {message && <div className="alert alert-info">{message}</div>}
      <h5>Payment History</h5>
      {loading ? (
        <p>Loading payments...</p>
      ) : payments.length === 0 ? (
        <p>No payments made yet.</p>
      ) : (
        <table className="table table-striped" style={{ maxWidth: 700 }}>
          <thead>
            <tr>
              <th>Amount</th>
              <th>Receipt</th>
              <th>Paid On</th>
            </tr>
          </thead>
          <tbody>
            {payments.map(p => (
              <tr key={p._id}>
                <td>â‚¹{p.amount}</td>
                <td>{p.receiptNumber}</td>
                <td>{new Date(p.paidAt).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
