import React, { useEffect, useState } from 'react';
import { Table, Spinner, Button } from 'react-bootstrap';
import api from '../../services/api';

export default function SecurityVisitors() {
  const [visitors, setVisitors] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchVisitors = async () => {
    try {
      const res = await api.get('/visitors');
      setVisitors(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVisitors();
  }, []);

  const approveVisitor = async (id) => {
    try {
      await api.patch(`/visitors/${id}/approve`);
      fetchVisitors();
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <Spinner animation="border" />;

  return (
    <div>
      <h3>Visitor Log</h3>
      {visitors.length === 0 ? <p>No visitors found.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Expected By</th>
              <th>Status</th>
              <th>Arrived At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {visitors.map(v => (
              <tr key={v._id}>
                <td>{v.name}</td>
                <td>{v.phone}</td>
                <td>{v.expectedBy?.name || 'N/A'}</td>
                <td>{v.status}</td>
                <td>{v.arrivedAt ? new Date(v.arrivedAt).toLocaleString() : '-'}</td>
                <td>
                  {v.status === 'pending' ? (
                    <Button variant="success" size="sm" onClick={() => approveVisitor(v._id)}>
                      Approve Arrival
                    </Button>
                  ) : (
                    <span>Approved</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
