import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ResidentDashboard from './pages/dashboards/ResidentDashboard';
import SecretaryDashboard from './pages/dashboards/SecretaryDashboard';
import TreasurerDashboard from './pages/dashboards/TreasurerDashboard';
import SecurityDashboard from './pages/dashboards/SecurityDashboard';
import MaintenanceDashboard from './pages/dashboards/MaintenanceDashboard';

function App() {
  const [user, setUser] = useState(() => {
    const saved = localStorage.getItem('livinglinkUser');
    return saved ? JSON.parse(saved) : null;
  });

  const login = (userData) => {
    setUser(userData);
    localStorage.setItem('livinglinkUser', JSON.stringify(userData));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('livinglinkUser');
  };

  // Protect dashboard routes
  const ProtectedRoute = ({ children, roles }) => {
    if (!user) return <Navigate to="/login" />;
    if (roles && !roles.includes(user.role)) return <Navigate to="/" />;
    return children;
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage user={user} logout={logout} />} />
        <Route path="/login" element={<LoginPage login={login} />} />
        <Route path="/register" element={<RegisterPage />} />

        <Route path="/resident/*" element={
          <ProtectedRoute roles={['resident']}>
            <ResidentDashboard logout={logout} />
          </ProtectedRoute>
        } />
        <Route path="/secretary/*" element={
          <ProtectedRoute roles={['secretary']}>
            <SecretaryDashboard logout={logout} />
          </ProtectedRoute>
        } />
        <Route path="/treasurer/*" element={
          <ProtectedRoute roles={['treasurer']}>
            <TreasurerDashboard logout={logout} />
          </ProtectedRoute>
        } />
        <Route path="/security/*" element={
          <ProtectedRoute roles={['security']}>
            <SecurityDashboard logout={logout} />
          </ProtectedRoute>
        } />
        <Route path="/maintenance/*" element={
          <ProtectedRoute roles={['maintenance']}>
            <MaintenanceDashboard logout={logout} />
          </ProtectedRoute>
        } />

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
