// frontend/src/components/Navbar.js
import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

export default function Navbar({ onMobileMenuToggle }) {  // Add this prop
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  function handleLogout() {
    logout();
    navigate('/');
  }

  return (
    <nav className="navbar navbar-expand navbar-dark" style={{ backgroundColor: 'var(--color-mid-dark-blue)' }}>
      <div className="container-fluid">
        {/* Mobile Menu Button - Only visible on mobile */}
        <button
          className="navbar-toggler d-md-none me-2"
          type="button"
          onClick={onMobileMenuToggle}
          style={{ border: 'none' }}
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        
        <span className="navbar-brand">Welcome, {user?.name}</span>
        <button className="btn btn-outline-light" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </nav>
  );
}
