import React from 'react';
import { Link } from 'react-router-dom';

const colors = {
  darkBlue: '#0D1B2A',
  navy: '#1B263B',
  blueGray: '#415A77',
  lightBlueGray: '#778DA9',
  offWhite: '#E0E1DD',
};

export default function LandingPage({ user, logout }) {
  return (
    <div style={{ minHeight: '100vh', backgroundColor: colors.darkBlue, color: colors.offWhite, display: 'flex', flexDirection: 'column' }}>
      <nav className="navbar navbar-expand-lg" style={{ backgroundColor: colors.navy }}>
        <div className="container">
          <Link className="navbar-brand" to="/" style={{ color: colors.offWhite, fontWeight: 'bold' }}>LivingLink</Link>
          <div>
            {user ? (
              <>
                <span className="me-3">Welcome, {user.name}</span>
                <button className="btn btn-outline-light btn-sm" onClick={logout}>Logout</button>
              </>
            ) : (
              <>
                <Link to="/login" className="btn btn-outline-light me-2 btn-sm">Login</Link>
                <Link to="/register" className="btn btn-outline-light btn-sm">Register</Link>
              </>
            )}
          </div>
        </div>
      </nav>

      <div className="container flex-grow-1 d-flex flex-column justify-content-center align-items-center text-center px-3">
        <h1 style={{ fontWeight: '900', fontSize: '3rem', marginBottom: '1rem' }}>Welcome to LivingLink</h1>
        <p style={{ maxWidth: 600, fontSize: '1.25rem' }}>
          The all-in-one residential society management system.
          Manage notices, complaints, payments, bookings, visitors, maintenance, and finances with ease.
        </p>
        {!user && (
          <Link to="/register" className="btn btn-primary btn-lg mt-4" style={{ backgroundColor: colors.blueGray, border: 'none' }}>
            Get Started
          </Link>
        )}
      </div>

      <footer className="text-center py-3" style={{ backgroundColor: colors.navy }}>
        <small>Â© 2024 LivingLink. All rights reserved.</small>
      </footer>
    </div>
  );
}
