import React, { useEffect, useState } from 'react';
import api from '../../../utils/api';

export default function FacilityBooking() {
  const [bookings, setBookings] = useState([]);
  const [date, setDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [bookingLoading, setBookingLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = () => {
    setLoading(true);
    api.get('/bookings')
      .then(res => {
        setBookings(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  };

  const handleBooking = async (e) => {
    e.preventDefault();
    setMessage('');
    if (!date) {
      setMessage('Please select a date');
      return;
    }
    setBookingLoading(true);
    try {
      await api.post('/bookings', { date });
      setMessage('Booking request submitted. Awaiting approval.');
      setDate('');
      fetchBookings();
    } catch (err) {
      setMessage(err.response?.data?.message || 'Booking failed');
    }
    setBookingLoading(false);
  };

  return (
    <div>
      <h3>Clubhouse Facility Booking</h3>
      <form onSubmit={handleBooking} className="mb-4">
        <div className="mb-3">
          <label>Select Date</label>
          <input type="date" className="form-control" value={date} onChange={e => setDate(e.target.value)} required />
        </div>
        {message && <div className="mb-3 text-info">{message}</div>}
        <button className="btn btn-primary" type="submit" disabled={bookingLoading}>
          {bookingLoading ? 'Booking...' : 'Book Facility'}
        </button>
      </form>

      <h5>Your Booking Requests</h5>
      {loading ? <p>Loading...</p> : (
        bookings.length === 0 ? <p>No booking requests found.</p> :
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Date</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map(b => (
                <tr key={b._id}>
                  <td>{new Date(b.date).toLocaleDateString()}</td>
                  <td>{b.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
      )}
    </div>
  );
}
