// frontend/src/components/Features/Features.js
import React from 'react';
import './Features.css';

export default function Features() {
  const features = [
    {
      icon: 'üí∞',
      title: 'Easy Payments',
      description: 'Secure online payment system with instant receipts and tracking'
    },
    {
      icon: 'üì¢',
      title: 'Instant Notices',
      description: 'Real-time announcements and updates from your society management'
    },
    {
      icon: 'üè¢',
      title: 'Facility Booking',
      description: 'Book common facilities with easy scheduling and approval system'
    },
    {
      icon: 'üõ°Ô∏è',
      title: 'Visitor Management',
      description: 'Pre-approve visitors and enhance security with digital records'
    },
    {
      icon: 'üîß',
      title: 'Maintenance Requests',
      description: 'Report issues and track repair status with our maintenance team'
    },
    {
      icon: 'üìä',
      title: 'Financial Transparency',
      description: 'Complete visibility into society finances and expense tracking'
    }
  ];

  return (
    <section className="features">
      <div className="container">
        <div className="section-header">
          <h2>Everything You Need in One Place</h2>
          <p>Comprehensive features designed for modern residential societies</p>
        </div>
        
        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
