import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import { Form, Button, Alert, Table, Spinner } from 'react-bootstrap';

export default function ResidentBookings() {
  const [date, setDate] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [bookings, setBookings] = useState([]);

  const fetchBookings = async () => {
    try {
      const res = await api.get('/bookings');
      setBookings(res.data.filter(b => b.user === undefined || b.user === null || b.user._id)); // user bookings
    } catch { }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  const submitBooking = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);
    try {
      await api.post('/bookings', { date });
      setSuccess('Booking request submitted.');
      setDate('');
      fetchBookings();
    } catch (err) {
      setError(err.response?.data?.message || 'Error submitting booking');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h3>Facility Booking - Clubhouse</h3>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form onSubmit={submitBooking} className="mb-4">
        <Form.Group controlId="bookingDate">
          <Form.Label>Select Date</Form.Label>
          <Form.Control 
            type="datetime-local" 
            value={date} 
            onChange={e => setDate(e.target.value)} 
            required 
          />
        </Form.Group>
        <Button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Request Booking'}</Button>
      </Form>

      <h5>Your Booking Requests</h5>
      {bookings.length === 0 ? <p>No bookings found.</p> :
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(b => (
              <tr key={b._id}>
                <td>{new Date(b.date).toLocaleString()}</td>
                <td>{b.status}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      }
    </div>
  );
}
