// frontend/src/components/Testimonials/Testimonials.js
import React from 'react';
import './Testimonials.css';

export default function Testimonials() {
  const testimonials = [
    {
      name: "Rahul Sharma",
      role: "Resident, Galaxy Apartments",
      content: "LivingLink has made society living so much easier. I can pay maintenance fees, book facilities, and stay updated with notices all from my phone!",
      avatar: "üë®‚Äçüíº"
    },
    {
      name: "Priya Patel",
      role: "Secretary, Sunshine Residency",
      content: "As a secretary, this platform has streamlined all our operations. Sending notices and managing complaints has never been simpler.",
      avatar: "üë©‚Äçüíº"
    },
    {
      name: "Amit Kumar",
      role: "Treasurer, Green Valley Society",
      content: "The financial transparency and payment tracking features have made my job so much easier. Highly recommended for any society treasurer!",
      avatar: "üë®‚Äçüéì"
    }
  ];

  return (
    <section className="testimonials">
      <div className="container">
        <div className="section-header">
          <h2>What Our Users Say</h2>
          <p>Join thousands of satisfied residents and society managers</p>
        </div>
        
        <div className="testimonials-grid">
          {testimonials.map((testimonial, index) => (
            <div key={index} className="testimonial-card">
              <div className="testimonial-content">
                <p>"{testimonial.content}"</p>
              </div>
              <div className="testimonial-author">
                <div className="avatar">{testimonial.avatar}</div>
                <div className="author-info">
                  <h4>{testimonial.name}</h4>
                  <p>{testimonial.role}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
