// frontend/src/components/Sidebar.js
import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Sidebar({ links }) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <div 
      className={`sidebar vh-100 p-3 d-flex flex-column position-relative ${isCollapsed ? 'collapsed' : ''}`}
      style={{ 
        width: isCollapsed ? 80 : 250, 
        backgroundColor: 'var(--color-dark-blue)', 
        color: 'var(--color-off-white)',
        transition: 'width 0.3s ease'
      }}
    >
      {/* Menu Toggle Button */}
      <button
        onClick={toggleSidebar}
        className="btn btn-sm btn-outline-light position-absolute"
        style={{ top: 15, right: 15, zIndex: 1000 }}
      >
        {isCollapsed ? '→' : '←'}
      </button>

      <h3 className="mb-4" style={{ opacity: isCollapsed ? 0 : 1, transition: 'opacity 0.2s ease' }}>
        {isCollapsed ? 'LL' : 'LivingLink'}
      </h3>
      
      <nav className="flex-grow-1">
        {links.map(({ to, label }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `d-block mb-2 p-2 rounded text-decoration-none ${isActive ? 'bg-primary text-white' : 'text-light'}`
            }
            style={{ 
              whiteSpace: 'nowrap',
              overflow: 'hidden',
              textOverflow: 'ellipsis'
            }}
          >
            {isCollapsed ? (
              <div className="d-flex flex-column align-items-center">
                <span>•</span>
                <small style={{ fontSize: '0.7rem' }}>{label.substring(0, 3)}</small>
              </div>
            ) : (
              label
            )}
          </NavLink>
        ))}
      </nav>
    </div>
  );
}
