import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getToken } from '../../services/auth';
import { Card, Spinner } from 'react-bootstrap';

export default function ResidentNotices() {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/notices', {
          headers: { Authorization: `Bearer ${getToken()}` }
        });
        setNotices(res.data);
      } catch (err) {
        console.error('Error fetching notices', err);
      } finally {
        setLoading(false);
      }
    };
    fetchNotices();
  }, []);

  if (loading) return <Spinner animation="border" variant="primary" />;

  return (
    <div>
      <h3>Notices</h3>
      {notices.length === 0 ? <p>No notices available.</p> :
       notices.map(n => (
        <Card key={n._id} className="mb-3">
          <Card.Body>
            <Card.Title>{n.title}</Card.Title>
            <Card.Text>{n.content}</Card.Text>
            <small>Posted by: {n.postedBy.name} ({n.postedBy.role}) on {new Date(n.createdAt).toLocaleString()}</small>
          </Card.Body>
        </Card>
      ))}
    </div>
  );
}
