import React, { useEffect, useState } from 'react';
import api from '../../../api/api';

export default function Complaints() {
  const [complaints, setComplaints] = useState([]);
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');

  async function fetchComplaints() {
    try {
      const { data } = await api.get('/complaints/mine');
      setComplaints(data);
    } catch {
      setComplaints([]);
    }
  }

  useEffect(() => {
    fetchComplaints();
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    setMessage('');
    if (!content.trim()) {
      setMessage('Please enter complaint content.');
      return;
    }
    try {
      await api.post('/complaints/mine', { content });
      setMessage('Complaint submitted anonymously');
      setContent('');
      fetchComplaints();
    } catch (err) {
      setMessage(err.response?.data?.message || 'Submission failed.');
    }
  }

  return (
    <div>
      <h3>Anonymous Complaints</h3>
      <form onSubmit={handleSubmit} className="mb-4" style={{ maxWidth: 600 }}>
        <textarea
          className="form-control mb-2"
          rows="3"
          placeholder="Write your complaint here..."
          value={content}
          onChange={e => setContent(e.target.value)}
          required
        />
        <button type="submit" className="btn btn-primary">Submit Complaint</button>
      </form>
      {message && <div className="alert alert-info">{message}</div>}
      <h5>Your Complaints Status</h5>
      <table className="table table-striped" style={{ maxWidth: 700 }}>
        <thead>
          <tr>
            <th>Content</th>
            <th>Status</th>
            <th>Submitted On</th>
          </tr>
        </thead>
        <tbody>
          {complaints.length === 0 && (
            <tr><td colSpan="3">No complaints submitted yet.</td></tr>
          )}
          {complaints.map(c => (
            <tr key={c._id}>
              <td>{c.content}</td>
              <td>{c.status}</td>
              <td>{new Date(c.createdAt).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
