// frontend/src/components/Sidebar.js
import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Sidebar({ links }) {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  const toggleMobileSidebar = () => {
    setIsMobileOpen(!isMobileOpen);
  };

  return (
    <>
      {/* Mobile Overlay - Only visible when sidebar is open on mobile */}
      {isMobileOpen && (
        <div 
          className="d-md-none position-fixed w-100 h-100"
          style={{
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            zIndex: 999,
            top: 0,
            left: 0
          }}
          onClick={() => setIsMobileOpen(false)}
        />
      )}

      {/* Mobile Menu Button - Only visible on mobile */}
      <button
        className="btn btn-primary d-md-none position-fixed"
        onClick={toggleMobileSidebar}
        style={{
          zIndex: 1001,
          bottom: 20,
          right: 20,
          borderRadius: '50%',
          width: '50px',
          height: '50px'
        }}
      >
        ☰
      </button>

      {/* Sidebar */}
      <div 
        className={`sidebar p-3 d-flex flex-column position-relative ${
          isCollapsed ? 'collapsed' : ''
        } ${isMobileOpen ? 'mobile-open' : 'mobile-closed'}`}
        style={{ 
          width: isCollapsed ? 60 : 250, 
          backgroundColor: 'var(--color-dark-blue)', 
          color: 'var(--color-off-white)',
          transition: 'width 0.3s ease, transform 0.3s ease',
          overflow: 'hidden',
          minHeight: '100vh',
          zIndex: 1000
        }}
      >
        {/* Menu Toggle Button */}
        <button
          onClick={toggleSidebar}
          className="btn btn-sm btn-outline-light position-absolute d-none d-md-block"
          style={{ top: 15, right: 15, zIndex: 1000 }}
        >
          {isCollapsed ? '→' : '←'}
        </button>

        {/* Close Button for Mobile */}
        <button
          onClick={() => setIsMobileOpen(false)}
          className="btn btn-sm btn-outline-light position-absolute d-md-none"
          style={{ top: 15, right: 15, zIndex: 1000 }}
        >
          ×
        </button>

        {/* Logo - Only show when expanded */}
        {!isCollapsed && (
          <h3 className="mb-4">LivingLink</h3>
        )}
        
        {/* Navigation Links - Only show when expanded */}
        {!isCollapsed && (
          <nav className="flex-grow-1">
            {links.map(({ to, label }) => (
              <NavLink
                key={to}
                to={to}
                className={({ isActive }) =>
                  `d-block mb-2 p-2 rounded text-decoration-none ${isActive ? 'bg-primary text-white' : 'text-light'}`
                }
                onClick={() => setIsMobileOpen(false)} // Close sidebar on mobile when link is clicked
              >
                {label}
              </NavLink>
            ))}
          </nav>
        )}
      </div>

      {/* Add CSS for mobile responsiveness */}
      <style>
        {`
          @media (max-width: 767.98px) {
            .sidebar {
              position: fixed !important;
              left: 0;
              top: 0;
              height: 100vh;
              transform: translateX(-100%);
            }
            
            .sidebar.mobile-open {
              transform: translateX(0);
              box-shadow: 2px 0 10px rgba(0, 0, 0, 0.3);
            }
            
            .sidebar.mobile-closed {
              transform: translateX(-100%);
            }
            
            .sidebar.collapsed {
              width: 250px !important; /* Force full width on mobile even when collapsed */
            }
          }
          
          @media (min-width: 768px) {
            .sidebar.mobile-closed {
              transform: translateX(0) !important;
            }
          }
        `}
      </style>
    </>
  );
}
