import React, { useEffect, useState } from 'react';
import api from '../../../api/api';

export default function VisitorLog() {
  const [visitors, setVisitors] = useState([]);
  const [message, setMessage] = useState('');

  async function fetchVisitors() {
    try {
      const { data } = await api.get('/visitors');
      setVisitors(data);
    } catch {
      setVisitors([]);
    }
  }

  useEffect(() => {
    fetchVisitors();
  }, []);

  async function approveVisitor(id) {
    setMessage('');
    try {
      await api.put(`/visitors/${id}/approve`);
      setMessage('Visitor approved');
      fetchVisitors();
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to approve visitor.');
    }
  }

  return (
    <div>
      <h3>Visitor Log</h3>
      {message && <div className="alert alert-info">{message}</div>}
      {visitors.length === 0 ? (
        <p>No visitors found.</p>
      ) : (
        <table className="table table-striped" style={{ maxWidth: 900 }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Contact</th>
              <th>Date</th>
              <th>Approved</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {visitors.map(v => (
              <tr key={v._id}>
                <td>{v.name}</td>
                <td>{v.contactNumber}</td>
                <td>{new Date(v.dateOfVisit).toLocaleDateString()}</td>
                <td>{v.approved ? 'Yes' : 'No'}</td>
                <td>
                  {!v.approved && (
                    <button className="btn btn-success btn-sm" onClick={() => approveVisitor(v._id)}>Approve</button>
                  )}
                  {v.approved && '—'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
