import React, { useEffect, useState } from 'react';
import api from '../../../api/api';

export default function MaintenanceFees() {
  const [payments, setPayments] = useState([]);
  const [residents, setResidents] = useState([]);

  useEffect(() => {
    async function fetchPayments() {
      try {
        const { data } = await api.get('/payments/all');
        setPayments(data);
      } catch {
        setPayments([]);
      }
    }
    async function fetchResidents() {
      try {
        const { data } = await api.get('/users/residents');
        setResidents(data);
      } catch {
        setResidents([]);
      }
    }
    fetchPayments();
    fetchResidents();
  }, []);

  // Create map of residentId to payment status
  const paymentMap = {};
  payments.forEach(p => paymentMap[p.resident._id] = true);

  return (
    <div>
      <h3>Maintenance Fee Payment Status</h3>
      {residents.length === 0 ? (
        <p>No residents found.</p>
      ) : (
        <table className="table table-striped" style={{ maxWidth: 700 }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Paid Maintenance Fee?</th>
            </tr>
          </thead>
          <tbody>
            {residents.map(r => (
              <tr key={r._id}>
                <td>{r.name}</td>
                <td>{r.email}</td>
                <td>{paymentMap[r._id] ? 'Yes' : 'No'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
