import React, { useEffect, useState } from 'react';
import api from '../../../utils/api';

export default function SocietyFinances() {
  const [finances, setFinances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/finances')
      .then(res => {
        setFinances(res.data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const totalSpent = finances.reduce((acc, f) => acc + f.amount, 0);

  return (
    <div>
      <h3>Society Finances</h3>
      {loading ? <p>Loading...</p> : (
        <>
          <h5>Total Spent: ₹{totalSpent.toFixed(2)}</h5>
          {finances.length === 0 ? <p>No finance records available.</p> :
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Description</th>
                  <th>Amount</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {finances.map(f => (
                  <tr key={f._id}>
                    <td>{f.description}</td>
                    <td>₹{f.amount.toFixed(2)}</td>
                    <td>{new Date(f.date).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          }
        </>
      )}
    </div>
  );
}
