import React from 'react';
import { NavLink } from 'react-router-dom';

const colors = {
  darkBlue: '#0D1B2A',
  navy: '#1B263B',
  blueGray: '#415A77',
  lightBlueGray: '#778DA9',
  offWhite: '#E0E1DD',
};

export default function Sidebar({ items, logout }) {
  return (
    <div style={{ width: '220px', backgroundColor: colors.navy, minHeight: '100vh', color: colors.offWhite, paddingTop: '1rem' }}>
      <h3 className="text-center mb-4" style={{ fontWeight: 'bold' }}>LivingLink</h3>
      <nav className="nav flex-column px-3">
        {items.map(({ label, to }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              'nav-link mb-2 rounded ' + (isActive ? 'bg-primary' : 'text-light')
            }
            style={{ fontWeight: '500' }}
          >
            {label}
          </NavLink>
        ))}
      </nav>
      <div className="text-center mt-auto p-3">
        <button className="btn btn-outline-light btn-sm" onClick={logout}>Logout</button>
      </div>
    </div>
  );
}
